import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import Form from './Form';

class CreateCourse extends Component {

    state = {
        title: '',
        firstName: this.props.context.authenticatedUser.Name,
        courseDescription: '',
        errors: [],
      }

      render() {
        const {
          title,
          courseDescription,
          errors,
        } = this.state;


    return (
        <main>
            <div class="wrap">
                <h2>Create Course</h2>
                <Form 
                cancel={this.cancel}
                errors={errors}
                submit={this.submit}
                submitButtonText="Create Course"
                elements={() => (
                    <React.Fragment>
                    <div class="main--flex">
                        <div>
                            <label for="title">Course Title</label>
                            <input 
                                id="title" 
                                name="title" 
                                type="text"
                                value={title} 
                                onChange={this.change} 
                                placeholder="Course Title" />

                            <p>By {this.context.authenticatedUser}</p>

                            <label for="courseDescription">Course Description</label>
                            <textarea 
                                id="courseDescription" 
                                name="courseDescription" 
                                type="textarea"
                                value={courseDescription} 
                                onChange={this.change} 
                                placeholder="Enter Course Description" />

                        </div>
                        <div>
                            <label for="estimatedTime">Estimated Time</label>
                            <input id="estimatedTime" name="estimatedTime" type="text" value="" />

                            <label for="materialsNeeded">Materials Needed</label>
                            <textarea id="materialsNeeded" name="materialsNeeded"></textarea>
                        </div>
                    </div>
                    </React.Fragment>
            )} />
            </div>
        </main>
    );
}

change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    console.log(this.context.authenticatedUser);

    this.setState(() => {
      return {
        [name]: value
      };
    });
  }

submit = () => {
    const { context } = this.props;
    const { from } = this.props.location.state || { from: { pathname: '/' } };
    context.actions.createCourse()
    .then( user => {
      if (user === null) {
        this.setState(() => {
          return { errors: [ 'Sign-in was unsuccessful' ] };
        });
      } else {
        this.props.history.push(from);
     }
    })
    .catch( err => {
      console.log(err);
      this.props.history.push('/error')
    })
  }

    cancel = () => {
        this.props.history.push('/');
      }
}

export default withRouter(CreateCourse);
