import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Course({context}) {
    let [courseDetails, addCourse] = useState([]);
    let { id } = useParams();


    useEffect(() => {
        context.data.getCourseById(id)
        .then((data) => addCourse(data))
        .catch((err) => {
            console.log(err);
        });
    }, []);
    let course;
    console.log(courseDetails)
    if (course !== null) {
        course = courseDetails.map((courseDetail) => 
        (
            <div class="main--flex">
                <div>
                    <h3 className="course--detail--title">Course</h3>
                    <h4 className="course--name">{courseDetail.title}</h4>
                    <p>By {courseDetail.owner.firstName} {courseDetail.owner.lastName}</p>
                    <p>{courseDetail.description}</p>
                </div>  
                <div>
                    <h3 className="course--detail--title">Estimated Time</h3>
                    <p>{courseDetail.estimatedTime}</p>

                    <h3 className="course--detail--title">Materials Needed</h3>
                    <ul className="course--detail--list">
                        <li>{courseDetail.materialsNeeded}</li>
                    </ul>
                </div>
            </div>

        ));
    }



    console.log(course);

    return (
        <div class="wrap">
                <h2>Course Detail</h2>
                <form>
                        {courseDetails}
                </form>
            </div>
    );
}

export default Course;
