import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Course({context}) {
    let [course, addCourse] = useState([]);
    let { id } = useParams();


    useEffect(() => {
        context.data.getCourseById(id)
        .then((data) => addCourse(data))
        .catch((err) => {
            console.log(err);
        });
    }, []);
    let courseDetails;
    console.log(course.length)
    if (course.length > 0) {

        courseDetails = course.map((data) => 
        (
            <div class="main--flex">
                <div>
                    <h3 class="course--detail--title">Course</h3>
                    <h4 class="course--name">{data.title}</h4>
                </div>  
                <div>
                    <h3 class="course--detail--title">Estimated Time</h3>
                    <p>14 hours</p>

                    <h3 class="course--detail--title">Materials Needed</h3>
                    <ul class="course--detail--list">
                        <li>

                        </li>
                    </ul>
                </div>
            </div>

        ));
    }



    console.log(course);

    return (
        <div class="wrap">
                <h2>Course Detail</h2>
                <form>
                        {courseDetails}
                </form>
            </div>
    );
}

export default Course;
