import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Course({context}) {
    let [course, addCourse] = useState([]);
    let { id } = useParams();


    useEffect(() => {
        context.data.getCourseById(id)
        .then((data) => addCourse(data))
        .catch((err) => {
            console.log(err);
        });
    }, []);
    let materials;
    console.log(course)
    if (course !== null) {
        materials = course.materialsNeeded.map((data) => 
        (
                        <li>{data}</li>
        ));
    }



    console.log(course);

    return (
        <div class="wrap">
                <h2>Course Detail</h2>
                <form>
                <div class="main--flex">
                <div>
                    <h3 class="course--detail--title">Course</h3>
                    <h4 class="course--name">{data.title}</h4>
                    <p>By {data.owner.firstName} {data.owner.lastName}</p>
                    <p>{data.description}</p>
                </div>  
                <div>
                    <h3 class="course--detail--title">Estimated Time</h3>
                    <p>{data.estimatedTime}</p>
                    <h3 class="course--detail--title">Materials Needed</h3>
                    <ul class="course--detail--list">
                        {materials}
                        </ul>
                </div>
            </div>
                </form>
            </div>
    );
}

export default Course;
