import React, { Component, useState, useEffect } from 'react';
import { useParams, withRouter } from 'react-router-dom';
import ReactMarkdown from "react-markdown";


class CourseDetail extends Component {

    state = {
        course: '',
        courseUserId: '',
        id: useParams(),
        errors: [],
    }

    render() {
        const {
          course,
          courseUserId,
          Id,
          errors,
        } = this.state


        this.props.context.data.getCourseById(this.id)
        .then(this.setState({ course }))
        .then(this.setState({ courseUserId: course.owner.courseUserId }))
        .catch((err) => {
            console.log(err);
        });

    let userId = this.props.context.authenticatedUser[ "User ID" ];


if (course.length !== 0) {
    console.log(course.owner)
    return (
        <main>
            <div className="actions--bar">
                {courseUserId === userId  
                    ?(<div className="wrap">
                        <a className="button" href={`/courses/${this.id}/update`}>Update Course</a>
                        <button className="button" onClick={this.handleDelete}>Delete Course</button>
                        <a className="button button-secondary" href="/">Return to List</a>
                    </div>)

                    :(<div className="wrap">
                        <a className="button button-secondary" href="/">Return to List</a>
                    </div>)
                    }

            </div>

             <div className="wrap">
                <h2>Course Detail</h2>
                <form>
                    <div className="main--flex">
                        <div>
                            <h3 className="course--detail--title">Course</h3>
                            <h4 className="course--name">{course.title}</h4>
                            <p>By {course.owner.firstName} {course.owner.lastName}</p>
                            <ReactMarkdown children={course.description} />
                        </div>
                        <div>
                            <h3 className="course--detail--title">Estimated Time</h3>
                            <p>{course.estimatedTime}</p>

                            <h3 className="course--detail--title">Materials Needed</h3>
                            <ul className="course--detail--list">
                                <ReactMarkdown children={course.materialsNeeded} />
                            </ul>
                        </div>
                    </div>
                </form>
            </div>
        </main>
    );
    } else {
        return (
            <p>Nothing to view here</p>
        );
    }
}


handleDelete = (event) => {
const { password } = this.props.context;
const username = this.props.context.authenticatedUser.Username;

    event.preventDefault();
    this.props.context.data.deleteCourse(username, password, this.Id)
    .then(errors => {
        if(errors){
        console.log(errors)
        } else {
        this.props.history.push('/');
        }
    })
    .catch( err => {
    console.log(err);
    })};



}
export default withRouter(CourseDetail);
