import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import Cookies from 'js-cookie';
import Form from './Form';

class CreateCourse extends Component {

    state = {
        title: '',
        courseDescription: '',
        estimatedTime: '',
        materialsNeeded: '',
        cookie: Cookies.get('authenticatedUser'),
        errors: [],
      }

      render() {
        const {
          title,
          courseDescription,
          estimatedTime,
          materialsNeeded,
          errors,
        } = this.state;


    return (
        <main>
            <div className="wrap">
                <h2>Create Course</h2>
                <Form 
                cancel={this.cancel}
                errors={errors}
                submit={this.submit}
                submitButtonText="Create Course"
                elements={() => (
                    <React.Fragment>
                    <div className="main--flex">
                        <div>
                            <label for="title">Course Title</label>
                            <input 
                                id="title" 
                                name="title" 
                                type="text"
                                value={title} 
                                onChange={this.change} 
                                placeholder="Course Title" />

                            <p>By {this.props.context.authenticatedUser.Name}</p>

                            <label for="courseDescription">Course Description</label>
                            <textarea 
                                id="courseDescription" 
                                name="courseDescription" 
                                type="textarea"
                                value={courseDescription} 
                                onChange={this.change} 
                                placeholder="Enter Course Description" />

                        </div>
                        <div>
                            <label for="estimatedTime">Estimated Time</label>
                                <input 
                                    id="estimatedTime" 
                                    name="tiestimatedTimetle" 
                                    type="text"
                                    value={estimatedTime} 
                                    onChange={this.change} 
                                    placeholder="Estimated Time" />

                            <label for="materialsNeeded">Materials Needed</label>
                            <textarea 
                                id="materialsNeeded" 
                                name="materialsNeeded" 
                                type="textarea"
                                value={materialsNeeded} 
                                onChange={this.change} 
                                placeholder="Enter Materials Needed" />
                        </div>
                    </div>
                    </React.Fragment>
            )} />
            </div>
        </main>
    );
}

change = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    console.log(this.props.context.authenticatedUser.password)


    this.setState(() => {
      return {
        [name]: value
      };
    });
  }

submit = () => {
    const { context } = this.props;
    const userName = context.authenticatedUser.Username;

    const { title, courseDescription, estimatedTime, materialsNeeded } = this.state;
    const body = {

        'title': title,
        'courseDescription': courseDescription,
        'estimatedTime': estimatedTime,
        'materialsNeeded': materialsNeeded,
        'userId': ''

    }
    context.actions.createCourse(userName, title, courseDescription, estimatedTime, materialsNeeded)
    .then( user => {
      if (user === null) {
        this.setState(() => {
          return { errors: [ 'Could not create course' ] };
        });
      } else {
        this.props.history.push(`/courses/1`);
     }
    })
    .catch( err => {
      console.log(err);
      this.props.history.push('/error')
    })
  }

    cancel = () => {
        this.props.history.push('/');
      }
}

export default withRouter(CreateCourse);
